import React, { useContext } from 'react'
import { GlobalContext } from '../Context/GlobalState';

export const Transaction = ({ transaction }) => {

    const { DelTransaction, UpdateTransaction } = useContext(GlobalContext);

    function EditTrans(){
        let desc = prompt('Enter description:');
        let amount = +prompt("Enter amount");

        let transaction = {
            id: transaction.id,
            desc: transaction.desc,
            amount: transaction.amount
        }
        
        UpdateTransaction(transaction);

    }
    return (
        <li className={`transaction ${transaction.amount < 0 ? 'red' : 'green'}`}>
            <button className='EditBtn btn' onClick={EditTrans}>Edit</button>
            <span>{transaction.desc}</span>
            <span>${transaction.amount}</span>
            <button className='DelBtn btn' onClick={() => { DelTransaction(transaction.id) }}>X</button>
        </li>
    )
}
