import React from 'react'
import { useSelector } from 'react-redux';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export const Balance = () => {

    const { Alltransactions } = useSelector(state => state);

    const amounts = Alltransactions.transactions.map(transaction => transaction.amount);

    let balance = 0;
    for (let i = 0; i < amounts.length; i++) {
        balance += amounts[i];
    }

    useEffect(() => {
        const notify = () => toast("Fetching the Model Do not Close", {
          position: toast.POSITION.TOP_CENTER,
          autoClose: 60000
        });
    
        notify();
      }, [])

    return (

        <div className='balance-box text-center'>
            
            <h6>Your Balance:</h6>
            <h2>${balance > 0 ? balance : <ToastContainer />}</h2>
            <hr />
        </div>

    )
}
