import React from 'react'
import { useState } from 'react';
// import { useContext } from 'react';
// import { GlobalContext } from '../Context/GlobalState';
import { useDispatch } from 'react-redux/es/exports';
import { DelTrans } from '../actions/allActions';
import { EditTrans } from '../actions/allActions';

export const Transaction = ({ transaction }) => {
    const [editOn, seteditOn] = useState(true);
    const dispatch = useDispatch();

    // const { DelTransaction, UpdateTransaction } = useContext(GlobalContext);

    function EditTransaction() {

        let trans = {
            id: transaction.id,
            desc: prompt('Enter description:'),
            amount: +prompt("Enter amount")
        }

        // console.log(`trans.id ${trans.id}`);
        // console.log(`transaction.id ${transaction.id}`);

        // UpdateTransaction(trans, transaction); // using context API

        dispatch(EditTrans(transaction, trans))

    }

    const RemoveTrans = (id) => {
        dispatch(DelTrans(id));
    }
    return (

        editOn ?
            <div className="wrapperPopUp">
                <div className='editPopBox'>
                <form>
                <div>
                    <label htmlFor="desc">Enter new Description</label><br />
                    <input type="text" placeholder='New Description' id='desc' required value={desc} onChange={(e)=>{setDesc(e.target.value)}}/>
                </div>
                {/* <div>
                    <label htmlFor="amount">Enter Amount</label>
                    <br />
                    <input type="number" placeholder='Amount' id='amount' required value={amount} onChange={(e)=>{setAmount(e.target.value)}}/>
                </div> */}
                <button type='submit' className='addBtn'>Edit Transaction</button>
            </form>
                </div>
            </div>

            :
            <li className={`transaction ${transaction.amount < 0 ? 'red' : 'green'}`}>
                <button className='EditBtn btn' onClick={EditTransaction}><i class="material-icons">edit</i></button>
                <span>{transaction.desc}</span>
                <span>${transaction.amount}</span>
                <button className='DelBtn btn' onClick={() => { RemoveTrans(transaction.id) }}><i class="material-icons">delete</i></button>
            </li>

    )
}
