import React, { createContext, useReducer } from "react";
import AppReducer from "./AppReducer";

const initialTransactions = {
    transactions: []
};
// { id: 1, desc: "Salary", amount: 500 },
// { id: 2, desc: "Car", amount: -200 },
// { id: 3, desc: "Fuel", amount: -100 },


// Create context
export const GlobalContext = createContext(initialTransactions);


// Provider
export const GlobalProvider = ({ children }) => {

    let [state, dispatch] = useReducer(AppReducer, initialTransactions);

    function AddTransaction(transaction) {
        dispatch(
            {
                type: "Add-transaction",
                payload: transaction
            }
        )
    }
    function DelTransaction(id) {
        // console.log(id);
        dispatch(
            {
                type: "Del-transaction",
                payload: id
            }
        )
    }
    function UpdateTransaction(transaction) {
        
        
        dispatch(
            {
                type: "Update-transaction",
                payload: transaction
            }
        )
    }
    return (
        <GlobalContext.Provider value={
            {
                transactions: state.transactions,
                AddTransaction,
                DelTransaction
            }
        }>
            {children}
        </GlobalContext.Provider>
    )
}
