export default (state, action) =>{
    switch(action.type){
        case "Add-transaction":
            return{
                ...state,
                transactions: [action.payload, ...state.transactions]
            }
        case "Del-transaction":
            return{
                ...state,
                transactions: [state.transactions.filter((item)=>{item !== action.payload})]
            }
        default:
            return state;
    }
}
