import React, { useContext } from 'react'
import { GlobalContext } from './Context/GlobalState';

export const IncomeExpense = () => {
    let { transactions } = useContext(GlobalContext);

    const amounts = transactions.map((transaction)=>{return transaction.amount});

    const income = amounts.filter(item=>{return item > 0}).reduce((amount, item)=>{return amount += item}, 0);

    const expense = amounts.filter(item=>{return item < 0}).reduce((amount, item)=>{return amount += item}, 0) * -1;

    console.log(expense);

    return (
        <div className='in-ex-wrapper'>
            <div>
                <h4>Income</h4>
                <p className='income'>${ income }</p>
            </div>
            <div>
                <h4>Expense</h4>
                <p className="expense">${ expense }</p>
            </div>
        </div>
    )
}
