import React from 'react'

export const Transaction = ({ transaction }) => {
    return (
        <li className='transaction'><span>{transaction.desc}</span> <span>${transaction.amount}</span>
            <button className='DelBtn' onClick={() => { DelTransaction(transaction.id) }}>X</button></li>
    )
}
