import React, { useContext, useEffect, useState } from 'react'
import { GlobalContext } from './Context/GlobalState'

export const AddTransaction = () => {

    const [text, setText] = useState('');
    const [amount, setAmount] = useState(0);

    const { addTransaction } = useContext(GlobalContext);
    
      const submit = e => {
        e.preventDefault();

        let newTransaction = {
            id: Math.floor(Math.random * 999999),
            text,
            amount: +amount
        }

        addTransaction(newTransaction);
    }
    
    return (
        <>
            <h3>Add Transactions here</h3>
            <form onSubmit={submit}><br />
                <div className="form-control">
                    <label htmlFor="text">Text</label>
                    <input type="text" placeholder='Enter transaction name...' value={text} onChange={e=>{setText(e.target.value)}}/>
                </div><br />
                <div className="form-control">
                    <label htmlFor="amount">Amount <br />Neg--- expense Positive--- Income</label>
                    <input type="number" placeholder='Enter transaction amount...' value={amount} onChange={e=>{setAmount(e.target.value)}}/>
                </div><br />
                <button className='AddBtn'>Add Transaction</button>
            </form>
        </>
    )
}
