const constants = {
    ADD_TRANS: "ADD_TRANS",
    DEL_TRANS: "DEL_TRANS",
    UPDATE_TRANS: "UPDATE_TRANS",
}

export const AddTrans = (transaction) => {
    return (dispatch) =>{
        dispatch({
            type: constants.ADD_TRANS,
            payload: transaction,
        })
    }
}

export const DelTrans = (id) => {
    return (dispatch) => {
        dispatch()
    }
}



export default constants;
